set_option linter.unusedTactic false
set_option linter.unusedVariables false

def mono (f : ℤ → ℤ) : Prop :=
  ∀ n m, n ≤ m → f n ≤ f m

def shift_left1 (f : ℤ → ℤ) (x : ℤ) : ℤ :=
  f (x + 1)

def preserved_by_shifting_left (P : (ℤ → ℤ) → Prop) : Prop :=
  ∀ f, P f → P (shift_left1 f)

theorem mono_pl : preserved_by_shifting_left mono := by
